Result




section &lt;- read_html(url) |&gt; 
  html_elements("section")
tibble(
  title = section |&gt; 
    html_element("h2") |&gt; 
    html_text2(),
  released = section |&gt; 
    html_element("p") |&gt; 
    html_text2() |&gt; 
    str_remove("Released: ") |&gt; 
    parse_date(),
